import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatIconModule } from '@angular/material/icon';
import { FormsModule } from '@angular/forms';
import { MatMenuModule } from '@angular/material/menu';
import { AuthService } from '@tronox-web/util-library';
@Component({
  selector: 'lib-toolbar',
  imports: [
    CommonModule,
    MatToolbarModule,
    MatFormFieldModule,
    MatInputModule,
    MatIconModule,
    FormsModule,
    MatMenuModule,
  ],
  templateUrl: './toolbar.component.html',
  styleUrl: './toolbar.component.scss',
})
export class ToolbarComponent implements OnInit {
  @Input() className: any;
  @Input() drawerContent: any;
  @Input() showSearch = false;
  @Input() imageSrc = '';
  @Input() searchQuery: any;
  userName: any;
  constructor(private authService: AuthService) {}
  ngOnInit(): void {
    this.userName = localStorage.getItem('tronox_username');
  }

  onLogoutClick() {
    this.authService.logout();
  }
}
<mat-toolbar [class]="className">
  @if(showSearch){
  <ng-container>
    <div class="search-container">
      <input
        type="text"
        id="searchInput"
        class="search-input"
        placeholder="Search..."
      />
      <button class="search-button">
        <span class="search-icon"><mat-icon>search</mat-icon></span>
      </button>
    </div>
  </ng-container>

  <span class="spacer"></span>

  <!-- User Dropdown -->
  <div class="user-dropdown" [matMenuTriggerFor]="userMenu">
    <span class="user-name">Hello, {{ userName }}</span>
    <mat-icon class="caret-icon">keyboard_arrow_down</mat-icon>
  </div>

  <mat-menu #userMenu="matMenu">
    <button mat-menu-item (click)="onLogoutClick()">
      <mat-icon>logout</mat-icon>
      Logout
    </button>
  </mat-menu>

  } @if (imageSrc) {
  <img [src]="imageSrc" alt="Toolbar Image" class="toolbar-image" />
  } @if(drawerContent){
  {{ drawerContent }}
  }
</mat-toolbar>
