import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatSidenavModule } from '@angular/material/sidenav';
import { ToolbarComponent } from '../toolbar/toolbar.component';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { TileLayoutComponent } from '../tile-layout/tile-layout.component';
import { TileService } from '@tronox-web/util-library';
import { MatIconModule } from '@angular/material/icon';
@Component({
  selector: 'lib-drawer-layout',
  standalone: true,
  imports: [
    CommonModule,
    MatSidenavModule,
    ToolbarComponent,
    TileLayoutComponent,
    MatIconModule,
  ],
  templateUrl: './drawer-layout.component.html',
  styleUrl: './drawer-layout.component.scss',
})
export class DrawerLayoutComponent implements OnInit {
  @Input() isDrawerOpen = true;
  tiles: any = [];
  cols: any = 3;
  isSubMenuOpen: any = {};
  uniqueTiles: any = [];
  selectedSubMenuApp: any;
  tilesCopy: any;
  constructor(
    private readonly breakpointObserver: BreakpointObserver,
    private readonly tileService: TileService
  ) {
    this.breakpointObserver
      .observe([
        Breakpoints.XSmall,
        Breakpoints.Small,
        Breakpoints.Medium,
        Breakpoints.Large,
        Breakpoints.XLarge,
      ])
      .subscribe((result) => {
        if (result.breakpoints[Breakpoints.XSmall]) {
          this.cols = 1;
        } else if (result.breakpoints[Breakpoints.Small]) {
          this.cols = 2;
        } else if (result.breakpoints[Breakpoints.Medium]) {
          this.cols = 3;
        } else if (result.breakpoints[Breakpoints.Large]) {
          this.cols = 4;
        } else if (result.breakpoints[Breakpoints.XLarge]) {
          this.cols = 5;
        }
      });
  }
  ngOnInit(): void {
    this.tileService.getTiles().subscribe((tiles: any) => {
      Object.entries(tiles).forEach(([key, value]) => {
        this.tiles.push(value);
      });
      this.getUniqueTiles(this.tiles);
      this.tilesCopy = JSON.parse(JSON.stringify(this.tiles));
    });
  }

  toggleSubMenu(): void {
    // Toggle the visibility of the submenu
    this.isSubMenuOpen = !this.isSubMenuOpen;
  }

  getUniqueTiles(tiles: any) {
    tiles.forEach((tile: any) => {
      let found: any = this.uniqueTiles.find(
        (ut: any) => ut?.appName == tile?.appName
      );
      if (!found) {
        this.uniqueTiles.push(tile);
      }
      found = null;
    });
  }

  updateTileView(appName: string) {
    this.selectedSubMenuApp = appName;
  }
}
<mat-drawer-container class="full-page-container">
  <!-- Left Drawer -->
  <mat-drawer mode="side" [opened]="isDrawerOpen" class="drawer">
    <lib-toolbar
      [className]="'left-drawer'"
      [imageSrc]="'assets/tronox_logo.png'"
    >
    </lib-toolbar>

    <!-- Drawer Menu Items -->
    <div class="drawer-menu">
      <!-- Menu item with submenu toggle -->
      <button mat-button class="menu-item" (click)="toggleSubMenu()">
        <mat-icon>apps</mat-icon>
        <span>My Apps</span>
      </button>

      <!-- Submenu under My Apps -->
      <div *ngIf="isSubMenuOpen" class="submenu">
        @for(tile of uniqueTiles; track tile.appNameSpec){
        <button
          mat-button
          class="submenu-item"
          (click)="updateTileView(tile.appName)"
        >
          <mat-icon>folder_open</mat-icon>
          <span>{{ tile.appName }}</span>
        </button>
        }
        <!-- <button mat-button class="submenu-item">
          <mat-icon>folder_open</mat-icon>
          <span>App 2</span>
        </button>
        <button mat-button class="submenu-item">
          <mat-icon>folder_open</mat-icon>
          <span>App 3</span>
        </button> -->
      </div>

      <!-- Other main menu items -->
      <button mat-button class="menu-item">
        <mat-icon>dashboard</mat-icon>
        <span>Dashboard</span>
      </button>
      <button mat-button class="menu-item">
        <mat-icon>settings</mat-icon>
        <span>Settings</span>
      </button>
      <button mat-button class="menu-item">
        <mat-icon>help_outline</mat-icon>
        <span>Help & Support</span>
      </button>
    </div>
  </mat-drawer>

  <!-- Main Content -->
  <mat-drawer-content class="main-content">
    <lib-toolbar [className]="'right-drawer'" [showSearch]="true"></lib-toolbar>
    <div class="grid-container">
      <lib-tile-layout
        [selectedAppFromSubmenu]="selectedSubMenuApp"
        [tiles]="tiles"
        [tilesCopy]="tilesCopy"
      ></lib-tile-layout>
    </div>
  </mat-drawer-content>
</mat-drawer-container>
